import React, { useEffect, useState } from 'react'

import { FlatList } from 'react-native'

import Screen from '../components/Screen'
import UserListItem from '../components/UserListItem'
import ListItemSeparator from '../components/ListItemSeparator'

import { connect } from 'react-redux'
import { getAllProfiles } from '../actions/Profile'

const users = [
  {
    id: 1,
    name: 'John Doe',
    blood_group: 'O+',
  },
  {
    id: 2,
    name: 'John Cena',
    blood_group: 'O-',
  },
  {
    id: 3,
    name: 'Bruce Wayne',
    blood_group: 'A+',
  },
  {
    id: 4,
    name: 'Clark Kent',
    blood_group: 'A-',
  },
  {
    id: 5,
    name: 'Diana Prince',
    blood_group: 'AB+',
  },
  {
    id: 6,
    name: 'Tony Stark',
    blood_group: 'AB-',
  },
  {
    id: 7,
    name: 'Steve Rogers',
    blood_group: 'B+',
  },
  {
    id: 8,
    name: "T'Challa",
    blood_group: 'B-',
  },
  {
    id: 9,
    name: 'Bucky',
    blood_group: 'O+',
  },
  {
    id: 10,
    name: 'Bruce Banner',
    blood_group: 'O-',
  },
  {
    id: 12,
    name: 'Natasha',
    blood_group: 'A+',
  },
  {
    id: 13,
    name: 'Berry Allan',
    blood_group: 'O+',
  },
]

const UsersListScreen = ({ navigation, getAllProfiles, profiles }) => {
  const [profs, setProfs] = useState([])
  useEffect(() => {
    getAllProfiles()
    setProfs(profiles)
  }, [getAllProfiles])
  // console.log(profiles)
  console.log(profs)
  return (
    <Screen>
      {profs.length > 0 && (
        <FlatList
          data={profs}
          keyExtractor={(profile) => profile.key}
          renderItem={({ item }) => (
            <UserListItem
              name={item.name}
              bloodGroup={item.blood_group}
              onPress={() => navigation.navigate('Profile_Page')}
            />
          )}
          ItemSeparatorComponent={ListItemSeparator}
        />
      )}
    </Screen>
  )
}

const mapStateToProps = (state) => ({
  profiles: state.Profile.profiles,
})

export default connect(null, { getAllProfiles })(UsersListScreen)
