import React from 'react'
import { Image, View, TouchableHighlight, StyleSheet } from 'react-native'

import AppView from '../components/AppView'
import AppText from '../components/AppText'

import colors from '../config/colors'
import { Feather } from '@expo/vector-icons'

const UserListItem = ({ name, bloodGroup, onPress }) => {
  return (
    <TouchableHighlight underlayColor={colors.light} onPress={onPress}>
      <AppView style={styles.userListItem}>
        <Image source={require('../assets/user.png')} style={styles.avatar} />
        <View style={styles.listItemDetail}>
          <View>
            <AppText style={styles.full_name}>{name}</AppText>
            <AppText style={styles.blood_group}>
              Blood Group: {bloodGroup}
            </AppText>
          </View>
          <Feather name='chevron-right' size={14} color={colors.blood} />
        </View>
      </AppView>
    </TouchableHighlight>
  )
}

const styles = StyleSheet.create({
  userListItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingTop: 5,
    paddingBottom: 5,
  },
  avatar: {
    width: 60,
    height: 60,
    borderRadius: 30,
    marginRight: 15,
  },
  listItemDetail: {
    flexDirection: 'row',
    flex: 1,
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  full_name: {
    fontSize: 16,
  },
  blood_group: {
    fontSize: 18,
    fontWeight: 'bold',
  },
})

export default UserListItem
