import {
  SUBMIT_SUCCESS,
  SUBMIT_FAIL,
  GET_POSTS_SUCCESS,
  GET_POSTS_FAIL,
  GET_SINGLE_SUCCESS,
  GET_SINGLE_FAIL,
} from './types'

import { db } from '../config/firebase'

/* ============================== */
/* ADD NEW POST */
/* ============================== */
export const addNewPOST = (data) => async (dispatch) => {
  try {
    const res = db.ref('/').child('posts').push(data)
    dispatch({
      type: SUBMIT_SUCCESS,
      payload: res,
    })
  } catch (err) {
    dispatch({
      type: SUBMIT_FAIL,
    })
  }
}
