import React, { useEffect } from 'react'
import { View, Text, StyleSheet } from 'react-native'

import { AppForm, AppFormField, SubmitButton } from '../components/forms'
import colors from '../config/colors'

import { connect } from 'react-redux'
import { addNewPOST, getPosts } from '../actions/Post'

const AddPostScreen = ({ addNewPOST, getPosts, posts }) => {
  useEffect(() => {
    getPosts()
  }, [getPosts])
  return (
    <View>
      <AppForm
        initialValues={{ title: '', detail: '' }}
        onSubmit={(values) => addNewPOST(values)}
      >
        <View style={styles.form}>
          <AppFormField placeholder='Title' name='title' />
          <AppFormField placeholder='Detail' name='detail' />
          <SubmitButton title='Add Post' />
        </View>
      </AppForm>

      <View>
        {posts.map((post) => (
          <Text>
            {post.title} - {post.detail}
          </Text>
        ))}
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: colors.white,
  },
  heading: {
    marginVertical: 15,
  },
  form: {
    width: '100%',
    marginVertical: 5,
    paddingRight: 35,
    paddingLeft: 35,
  },
})

const mapStateToProps = (state) => ({
  posts: state.Post.posts,
})

export default connect(mapStateToProps, { addNewPOST, getPosts })(AddPostScreen)
