import { CREATE_USER_SUCCESS, CREATE_USER_FAIL } from './types'
import { auth, firebase2, db, firestore } from '../config/firebase'

// import * as firebase from 'firebase'
// import 'firebase/firestore'

export const signUp = (name, email, password, password2) => async (
  dispatch
) => {
  try {
    if (password !== password2) {
      alert("Passwords don't match.")
      return
    }
    firebase2
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then((response) => {
        const uid = response.user.uid
        const data = {
          id: uid,
          email,
          name,
        }
        const usersRef = firestore.collection('profiles')
        usersRef
          .doc(uid)
          .set(data)
          .then(() => {
            // navigation.navigate('Home', { user: data })
            console.log(response)
          })
          .catch((error) => {
            alert(error)
          })
      })
      .catch((error) => {
        alert(error)
      })
  } catch (error) {
    var errorCode = error.code
    var errorMessage = error.message
    console.log(errorCode)
    console.log(errorMessage)
    dispatch({
      type: CREATE_USER_FAIL,
    })
  }
}

export const login = (email, password) => {
  try {
    firebase2
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((response) => {
        const uid = response.user.uid
        const usersRef = firestore.collection('profiles')
        usersRef
          .doc(uid)
          .get()
          .then((firestoreDocument) => {
            if (!firestoreDocument.exists) {
              alert('User does not exist anymore.')
              return
            }
            const user = firestoreDocument.data()
          })
          .catch((error) => {
            alert(error)
          })
      })
      .catch((error) => {
        alert(error)
      })
  } catch (error) {
    console.log(error)
  }
}
