import React from 'react'
import { View, Text, StyleSheet } from 'react-native'

import { AppForm, AppFormField, SubmitButton } from '../components/forms'
import colors from '../config/colors'

import { connect } from 'react-redux'
import { addNewPOST } from '../actions/Post'

const AddPostScreen = () => {
  return (
    <View>
      <AppForm
        initialValues={{ title: '', detail: '' }}
        onSubmit={(values) => console.log(valus)}
      >
        <View style={styles.form}>
          <AppFormField placeholder='Title' name='title' />
          <AppFormField placeholder='Detail' name='detail' />
          <SubmitButton title='Add Post' />
        </View>
      </AppForm>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: colors.white,
  },
  heading: {
    marginVertical: 15,
  },
  form: {
    width: '100%',
    marginVertical: 5,
    paddingRight: 35,
    paddingLeft: 35,
  },
})
export default connect(null, { addNewPOST })(AddPostScreen)
